import React from "react";
import { ListItem, Box } from "@mui/material";
import Divider from "@mui/material/Divider";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import Typography from "@mui/material/Typography";
import moment from "moment";

const ClientDetailTabBarUpdatesList = ({ update }) => {
  const updateTitle = {
    display: "flex",
  };
  return (
    <>
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Remy Sharp">
            {update.user_first_name[0].toUpperCase()}
            {update.user_last_name[0].toUpperCase()}
          </Avatar>
        </ListItemAvatar>
        <ListItemText
          primary={
            <Box sx={updateTitle}>
              <Typography variant="body2" color="text.primary" component="span">
                {`${update.user_first_name} ${update.user_last_name}`}
              </Typography>
              <Typography component="span">{update.commented_at}</Typography>
            </Box>
          }
          secondary={moment(update.comment).format(
            "dddd, MMMM Do YYYY, h:mm:ss a"
          )}
        />
      </ListItem>
      <Divider variant="inset" component="li" />
    </>
  );
};

export default ClientDetailTabBarUpdatesList;
