import * as React from "react";
import "./progress.css";
import { status } from "../helper/formSelectOptions";
import { DialogTitle, DialogContent } from "@mui/material";
import moment from "moment";

const ClientDetailTabBarProgressDates = ({ statusMapping, clientDetails }) => {
  return (
    <>
      <DialogTitle sx={{ display: "flex", justifyContent: "space-between" }}>
        Client Progress
      </DialogTitle>
      <DialogContent>
        <div className="deal-view">
          <div className="stepper">
            {clientDetails
              ? clientDetails.steps_updated_date.map((date) => {
                  status.map((s) => (
                    <div className="stepper_item_date">
                      <div className="stepper_item success">
                        <div className="stepper_title">{s.label}</div>
                      </div>
                      <div className="stepper_date">
                        {statusMapping[date.step_id] === s.label ? (
                          <div>
                            {moment(date.update_date).format("MM-DD-YYYY")}
                          </div>
                        ) : null}
                      </div>
                    </div>
                  ));
                })
              : null}
          </div>
        </div>
      </DialogContent>
    </>
  );
};

export default ClientDetailTabBarProgressDates;
