import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import ClientDetail from "./ClientDetail";
import moment from "moment";

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function ClientsTableRow({ client }) {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleCancel = () => {
    setOpen(false);
  };

  return (
    <>
      <TableRow
        sx={{
          "&:last-child td, &:last-child th": { border: 0 },
        }}
        onClick={handleClickOpen}
      >
        <TableCell component="th" scope="row" align="center">
          {client.first_name + " " + client.last_name}
        </TableCell>
        <TableCell align="center">{client.current_step_name}</TableCell>
        <TableCell align="center">{client.from_channel}</TableCell>
        <TableCell align="center">
          {client.is_enrolled === false ? "No" : "Yes"}
        </TableCell>
        <TableCell align="center">
          {moment(client.update_date).format("MM/DD/YYYY")}
        </TableCell>
      </TableRow>
      <ClientDetail open={open} handleCancel={handleCancel} client={client} />
    </>
  );
}
