import * as React from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import Home from "../Home";

export default function ClientDetailTabBar({ handleTabChange }) {
  const [value, setValue] = React.useState("information");

  const handleChange = (event, newValue) => {
    setValue(newValue);
    handleTabChange(value);
  };

  return (
    <Box sx={{ width: "100%" }}>
      <Tabs
        value={value}
        onChange={handleChange}
        textColor="secondary"
        indicatorColor="secondary"
      >
        <Tab value="information" label="Information">
          <Home />
        </Tab>
        <Tab value="progress" label="Progress" />
        <Tab value="updates" label="Updates" />
      </Tabs>
    </Box>
  );
}
