import * as React from "react";
import theme from "./Theme";
import { ThemeProvider, Alert, AlertTitle } from "@mui/material";
import Login from "./auth/Login";
import Register from "./auth/Register";
import Home from "./Home";
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  useNavigate,
  redirect,
} from "react-router-dom";
import PotentialClientTrackerApi from "./api";
import UserContext from "./userContext";

function App() {
  const [clients, setClients] = React.useState([]);
  const [user, setUser] = React.useState({});
  const [resStatus, setResStatus] = React.useState(null);
  const [userId, setUserId] = React.useState(
    JSON.parse(localStorage.getItem("userId"))
  );
  const navigate = useNavigate();

  React.useEffect(() => {
    const fetchClients = async () => {
      try {
        const res = await PotentialClientTrackerApi.getAllClients();
        setResStatus(res.status);
        setClients(res.data.clients);
        navigate("/");
      } catch (e) {
        setResStatus(e.response.status);
      }
    };
    fetchClients();
  }, [resStatus]);

  React.useEffect(() => {
    localStorage.setItem("userId", JSON.stringify(userId));
    const fetchUser = async (id) => {
      try {
        const res = await PotentialClientTrackerApi.getUser(id);
        setUser(res.data.user);
      } catch (e) {
        console.log(e);
      }
    };
    fetchUser(userId);
  }, [userId]);

  const handleLogin = (id) => {
    setResStatus(200);
    setUserId(id);
    navigate("/");
  };

  console.log(resStatus);
  console.log(userId);
  console.log(user);
  console.log(clients);

  return (
    <UserContext.Provider value={user}>
      <ThemeProvider theme={theme}>
        <div className="App">
          <Routes>
            <Route path="/register" element={<Register />} />
            <Route
              path="/login"
              element={<Login handleLogin={handleLogin} />}
            />
            {resStatus === 200 ? (
              <Route path="/" element={<Home clients={clients} />} />
            ) : (
              <Route path="*" element={<Navigate to="/login" replace />} />
            )}
          </Routes>
        </div>
      </ThemeProvider>
    </UserContext.Provider>
  );
}

export default App;
