import * as React from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import { DialogTitle, Box } from "@mui/material";
import PotentialClientTrackerApi from "../../../api";

export default function ClientDetailTabBarProgressAddDatesForm({
  open,
  handleClose,
  client,
  statusId,
}) {
  const handleSubmitAddDate = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const addData = {
      clientId: client.id,
      statusId,
      updateDate: data.get("updateDate"),
    };
    console.log(addData);
    // const res = await PotentialClientTrackerApi.addStatus()
  };
  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>Subscribe</DialogTitle>
      <DialogContent>
        <DialogContentText>Please choose the date for</DialogContentText>
        <Box component="form" onSubmit={handleSubmitAddDate}>
          <TextField
            autoFocus
            required
            margin="normal"
            id="updateDate"
            name="updateDate"
            type="date"
            fullWidth
            variant="standard"
          />
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button type="submit">Subscribe</Button>
          </DialogActions>
        </Box>
      </DialogContent>
    </Dialog>
  );
}
