import * as React from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Box,
  Button,
  TablePagination,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import ClientsTableRow from "./ClientsTableRow";
import AddClient from "../AddClient/AddClient";

export default function ClientsTable({ clients }) {
  const [open, setOpen] = React.useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(5);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleCancel = () => {
    setOpen(false);
  };

  console.log(clients);
  return (
    <Box>
      <Box sx={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="contained"
          sx={{ marginBottom: 3 }}
          onClick={handleClickOpen}
        >
          <AddIcon />
          New Client
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow
              sx={{
                fontWeight: "bold",
                borderBottomColor: "rgba(107, 85, 148)",
                // backgroundColor: "rgba(178, 144, 19)",
              }}
            >
              <TableCell align="center">Name</TableCell>
              <TableCell align="center">Status</TableCell>
              <TableCell align="center">Channel</TableCell>
              <TableCell align="center">Is Enrolled?</TableCell>
              <TableCell align="center">Latest Updated Date</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {clients
              ? clients.map((client) => (
                  <ClientsTableRow key={client.id} client={client} />
                ))
              : null}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[5, 10, 25]}
        component="div"
        count={clients.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
      <AddClient open={open} handleCancel={handleCancel} />
    </Box>
  );
}
