import * as React from "react";
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  Box,
  Grid,
  DialogContent,
  Link,
} from "@mui/material";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import ClientFormField from "./ClientFormField";

export default function ClientDetail({ open, handleCancel, client }) {
  const [isDisabled, setIsDisabled] = React.useState(true);

  const handleEdit = () => {
    setIsDisabled(false);
  };
  // make API call to get a specific client info
  // for now just for testing

  const mapping = [
    { label: "First Name", name: "firstName", value: client.name },
    { label: "Contact", name: "contact", value: client.contact },
  ];

  const handleSubmit = () => {};
  return (
    <Dialog open={open} onClose={handleClose} maxWidth={"md"} fullWidth>
      <DialogTitle>Client Information</DialogTitle>
      <DialogContent>
        {/* <DialogContentText>
          To subscribe to this website, please enter your email address here. We
          will send updates occasionally.
        </DialogContentText> */}
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <Grid container>
            {mapping.map((field) => (
              <ClientFormField field={field} disabled={isDisabled} />
            ))}
          </Grid>
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleCancel}>Cancel</Button>
        <Button onClick={handleEdit}>Edit</Button>
      </DialogActions>
    </Dialog>
  );
}
