import * as React from "react";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { citizenship, status, channels } from "../helper/formSelectOptions";

export default function ClientFormField({ client, disabled }) {
  return (
    <>
      <TextField
        margin="normal"
        required
        disabled={disabled}
        fullWidth
        id="firstName"
        label="First Name"
        name="firstName"
        defaultValue={client.first_name}
      />
      <TextField
        margin="normal"
        required
        disabled={disabled}
        fullWidth
        id="lastName"
        label="Last Name"
        name="lastName"
        defaultValue={client.last_name}
      />
      <TextField
        margin="normal"
        required
        disabled={disabled}
        fullWidth
        id="address"
        label="Address"
        name="address"
        defaultValue={client.address}
      />
      <TextField
        margin="normal"
        required
        fullWidth
        select
        id="citizenship"
        disabled={disabled}
        label="Citizenship"
        name="citizenship"
        defaultValue={client.citizenship}
      >
        {citizenship.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        margin="normal"
        required
        fullWidth
        select
        id="status"
        disabled={disabled}
        label="Status"
        name="status"
        defaultValue={client.current_step}
      >
        {status.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        margin="normal"
        required
        fullWidth
        select
        id="channel"
        disabled={disabled}
        label="How did you hear about us?"
        name="channel"
        defaultValue={client.from_channel}
      >
        {channels.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        margin="normal"
        required
        fullWidth
        radioGroup
        id="channel"
        disabled={disabled}
        label="How did you hear about us?"
        name="channel"
        defaultValue={client.from_channel}
      >
        {channels.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        margin="normal"
        required
        disabled={disabled}
        fullWidth
        id="diagnosis"
        label="Diagnosis"
        name="diagnosis"
        defaultValue={client.diagnosis}
      />
    </>
  );
}
