import React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";

const ClientDetailTabBarUpdatesForm = () => {
  return (
    <Box
      sx={{
        width: "80%",
        mx: "20px",
      }}
    >
      <TextField
        label="What's going on with this client"
        fullWidth
        id="update"
        color="primary"
        multiline
        rows={2}
        maxRows={4}
      />
    </Box>
  );
};

export default ClientDetailTabBarUpdatesForm;
