import * as React from "react";
import "./progress.css";
import { status } from "../../../helper/formSelectOptions";
import { DialogTitle, DialogContent, Chip, Divider } from "@mui/material";

import CloseIcon from "@mui/icons-material/Close";
import DeleteIcon from "@mui/icons-material/Delete";
import moment from "moment";
import $ from "jquery";
import Close from "@mui/icons-material/Close";

const ClientDetailTabBarProgressDates = ({ statusMapping, clientDetails }) => {
  React.useEffect(() => {
    $(".stepper_item").map((item, idx) => {
      if ($(idx).siblings().children().length != 0) {
        $(idx).addClass("success");
      }
    });
  });

  const chipstyle = {
    cursor: "pointer",
  };

  return (
    <>
      <DialogTitle sx={{ display: "flex", justifyContent: "space-between" }}>
        Client Progress
      </DialogTitle>
      <DialogContent>
        <div className="deal-view">
          <div className="stepper">
            {status.map((s) => (
              <>
                <div className="stepper_item_date" key={s.value}>
                  <div className="stepper_item ">
                    <div className="stepper_title">{s.label}</div>
                  </div>
                  <div className="stepper_date">
                    {clientDetails
                      ? clientDetails.status_updated_dates.map((date) => {
                          if (statusMapping[date.status_id] === s.label) {
                            return (
                              <>
                                {/* <Chip
                                  label={`${moment(date.update_date).format(
                                    "MM-DD-YYYY"
                                  )}&nbsp;&nbsp;&nbsp;&nbsp;DELETE`}
                                  sx={chipstyle}
                                  color="secondary"
                                  key={date.id}
                                /> */}
                                <div>
                                  {moment(date.update_date).format(
                                    "MM-DD-YYYY"
                                  )}
                                </div>
                              </>
                            );
                          }
                        })
                      : null}
                  </div>
                </div>
                <Divider />
              </>
            ))}
          </div>
        </div>
      </DialogContent>
    </>
  );
};

export default ClientDetailTabBarProgressDates;
