import * as React from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Box,
  Button,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import ClientsTableRow from "./ClientsTableRow";
import UserContext from "../../userContext";
import AddClient from "../AddClient/AddClient";

export default function ClientsTable() {
  const { clients } = React.useContext(UserContext);
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleCancel = () => {
    setOpen(false);
  };

  console.log(clients);
  return (
    <Box>
      <Box sx={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="contained"
          sx={{ marginBottom: 3, backgroundColor: "#6B5594" }}
          onClick={handleClickOpen}
        >
          <AddIcon />
          New Client
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow sx={{ fontWeight: "bold", backgroundColor: "#6B5594" }}>
              <TableCell align="center">Name</TableCell>
              <TableCell align="center">Status</TableCell>
              <TableCell align="center">Channel</TableCell>
              <TableCell align="center">Is Enrolled?</TableCell>
              <TableCell align="center">Latest Updated Date</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {clients.map((client) => (
              <ClientsTableRow key={client.id} client={client} />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <AddClient open={open} handleCancel={handleCancel} />
    </Box>
  );
}
