import * as React from "react";
import theme from "./Theme";
import { Button, ThemeProvider } from "@mui/material";
import Login from "./auth/Login";
import Register from "./auth/Register";
import Home from "./Home";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import PotentialClientTrackerApi from "./api";

function App() {
  const [clients, setClients] = React.useState([]);
  const [resStatus, setResStatus] = React.useState(null);
  const [err, setErr] = React.useState([]);
  React.useEffect(() => {
    const fetchClients = async () => {
      try {
        const clients = await PotentialClientTrackerApi.getAllClients();
        setClients(clients);
      } catch (e) {
        setResStatus(e.response.status);
        setErr(e.response.statusText);
      }
    };
    fetchClients();
  }, []);
  console.log(clients);
  console.log(resStatus);
  console.log(err);
  return (
    <ThemeProvider theme={theme}>
      <div className="App">
        <BrowserRouter>
          <Routes>
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />
            <Route
              path="/"
              element={
                resStatus === 401 ? <Navigate to="/login" replace /> : <Home />
              }
            />
          </Routes>
        </BrowserRouter>
      </div>
    </ThemeProvider>
  );
}

export default App;
