import * as React from "react";
import theme from "./Theme";
import { ThemeProvider, Alert, AlertTitle } from "@mui/material";
import Login from "./auth/Login";
import Register from "./auth/Register";
import Home from "./Home";
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  redirect,
} from "react-router-dom";
import PotentialClientTrackerApi from "./api";

function App() {
  const [clients, setClients] = React.useState([]);
  const [resStatus, setResStatus] = React.useState(null);
  const [userId, setUserId] = React.useState(null);

  React.useEffect(() => {
    const fetchClients = async () => {
      try {
        const { data } = await PotentialClientTrackerApi.getAllClients();
        setClients(data.clients);
      } catch (e) {
        setResStatus(e.response.status);
      }
    };
    fetchClients();
  }, [resStatus]);

  const handleLogin = (id) => {
    setResStatus(200);
    setUserId(id);
  };

  console.log(resStatus);
  console.log(userId);
  console.log(clients);

  return (
    <ThemeProvider theme={theme}>
      <div className="App">
        <BrowserRouter>
          <Routes>
            <Route path="/register" element={<Register />} />
            <Route
              path="/login"
              element={<Login handleLogin={handleLogin} />}
            />
            {userId ? (
              <Route path="/" element={<Home clients={clients} />} />
            ) : (
              <Route path="*" element={<Navigate to="/login" replace />} />
            )}
          </Routes>
        </BrowserRouter>
      </div>
    </ThemeProvider>
  );
}

export default App;
