import React, { useContext } from "react";
import moment from "moment";
import { Container, Typography, Box, Divider } from "@mui/material";
import ClientsTable from "./clients/clientsTable/ClientsTable";
import UserContext from "./userContext";
import Nav from "./Nav";

const Home = ({ userId }) => {
  // const { user } = useContext(UserContext);

  React.useEffect(() => {
    const fetchUser = async (id) => {
      try {
        const res = await PotentialClientTrackerApi.getUser(id);
        setUser(res.data.user);
      } catch (e) {
        console.log(e);
      }
    };
    fetchUser(userId);
  }, [userId]);

  const homeContainer = {
    marginTop: 5,
    width: "80%",
    mx: "auto",
  };
  const homeHeaderContainer = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };
  return (
    <>
      <Nav />
      <Box component="main" sx={homeContainer}>
        <Container sx={homeHeaderContainer}>
          <Typography variant="h2" component="h1">
            Welcome Back, <br />
            {user.firstname}!
          </Typography>
          <Box>
            Today is {moment().format("MMMM Do YYYY")}.
            <Box sx={{ my: 1 }} />
            Have a great {moment().format("dddd")}!
          </Box>
        </Container>
        <Divider sx={{ my: 5 }} />
        <ClientsTable />
      </Box>
    </>
  );
};

export default Home;
