import React, { useContext, useState } from "react";
import moment from "moment";
import { Container, Typography, Box, Divider } from "@mui/material";
import Image from "mui-image";
import ClientsTable from "./clients/clientsTable/ClientsTable";
import UserContext from "./userContext";
import Nav from "./Nav";
import PotentialClientTrackerApi from "./api";
import HomepageHeaderPic from "./HomepageHeaderPic.jpg";
import HomepageImageLayer from "./HomepageImageLayer.png";
import "./Home.css";
import Copyright from "./helper/copyright";

const Home = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [clients, setClients] = useState(null);

  React.useEffect(() => {
    const fetchUser = async (id) => {
      try {
        const res = await PotentialClientTrackerApi.getUser(id);
        setUser(res.data.user);
      } catch (e) {
        console.log(e);
      }
    };
    const fetchClients = async () => {
      try {
        const res = await PotentialClientTrackerApi.getAllClients();
        console.log(res.data.clients);
        setClients(res.data.clients);
      } catch (e) {
        console.log(e);
      }
    };
    fetchClients();
    fetchUser(userId);
  }, []);

  const homeContainer = {
    marginTop: 5,
    width: "80%",
    mx: "auto",
    height: "100vh",
  };

  const homeHeaderContainer = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };
  return (
    <>
      <Nav />
      <div className="ws-black w3-center home-imagewrapper">
        <div
          className="w3-content learntocodecontent "
          style={{ maxWidth: "1400px" }}
        >
          <h1 className="learntocodeh1 home-imagewrapper-title">
            CCACC
            <br />
            Potential Clients Tracker
          </h1>
          {/* <h3 class="learntocodeh3">
            With the world's largest web developer site.
          </h3>
          <br />
          <h4
            style={{
              color: "white",
              fontSize: "23px",
              fontWeight: "700",
              marginTop: "30px!important",
            }}
          >
            <a href="where_to_start.asp">Not Sure Where To Begin?</a>
          </h4> */}
        </div>
        <svg
          style={{ backgroundColor: "#FFCF1C", marginTop: "3500px" }}
          width="100%"
          height="70"
          viewBox="0 0 100 100"
          preserveAspectRatio="none"
        >
          <path
            id="wavepath"
            d="M0,0  L110,0C35,150 35,0 0,100z"
            fill="#472B7A"
          ></path>
        </svg>
      </div>
      <Box component="main" sx={homeContainer}>
        <Container sx={homeHeaderContainer}>
          <Typography variant="h2">
            Welcome Back, <br />
            {user ? user.firstname : "CCACC"}!
          </Typography>
          <Box>
            Today is {moment().format("MMMM Do YYYY")}.
            <Box sx={{ my: 1 }} />
            Have a great {moment().format("dddd")}!
          </Box>
        </Container>
        <Divider sx={{ my: 5 }} />
        <ClientsTable clients={clients} />
      </Box>
      <Copyright sx={{ mt: 8, mb: 2 }} />
    </>
  );
};

export default Home;
