import * as React from "react";
import "./progress.css";
import { status } from "../helper/formSelectOptions";
import PotentialClientTrackerApi from "../api";

const ClientDetailTabBarProgress = ({ client }) => {
  const [clientDetails, setClientDetails] = React.useState(null);

  const statusMapping = {
    1: "Initial Contact",
    2: "Information Session",
    3: "Onsite Visit",
    4: "Application Submission",
    5: "Phone Screening",
    6: "Interview",
    7: "Application Approved",
    8: "Application Denied",
    9: "Appealing",
    10: "Enrolled",
    11: "Not To Proceed",
  };

  React.useEffect(() => {
    const fetchClient = async (id) => {
      const { data } = await PotentialClientTrackerApi.getClient(id);
      setClientDetails(data.client);
    };
    fetchClient(client.id);
  }, []);

  console.log(clientDetails);

  return (
    <div className="deal-view">
      <div className="stepper">
        {clientDetails
          ? clientDetails.steps_updated_date.map((s) => {
              return (
                <div className="stepper_item success green">
                  <div className="stepper_title">
                    {statusMapping[s.step_id]}
                  </div>
                </div>
              );
            })
          : null}
      </div>
    </div>
  );
};

export default ClientDetailTabBarProgress;
