import React, { useContext, useState } from "react";
import moment from "moment";
import { Container, Typography, Box, Divider, Menu } from "@mui/material";
import Image from "mui-image";
import ClientsTable from "./clients/clientsTable/ClientsTable";
import UserContext from "./userContext";
import Nav from "./Nav";
import PotentialClientTrackerApi from "./api";
import HomepageHeaderPic from "./HomepageHeaderPic.jpg";
import HomepageImageLayer from "./HomepageImageLayer.png";
import "./Home.css";
import Copyright from "./helper/copyright";
import HomepageLogo from "./HomepageLogo.png";

const Home = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [clients, setClients] = useState(null);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  React.useEffect(() => {
    const fetchUser = async (id) => {
      try {
        const res = await PotentialClientTrackerApi.getUser(id);
        setUser(res.data.user);
      } catch (e) {
        console.log(e);
      }
    };
    const fetchClients = async () => {
      try {
        const res = await PotentialClientTrackerApi.getAllClients();
        console.log(res.data.clients);
        setClients(res.data.clients);
      } catch (e) {
        console.log(e);
      }
    };
    fetchClients();
    fetchUser(userId);
  }, []);

  const homeContainer = {
    marginTop: 5,
    width: "80%",
    mx: "auto",
    height: "100vh",
  };

  const homeHeaderContainer = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };

  const handleUsernameClick = (e) => {
    setAnchorEl(e.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <>
      <div className="home-imagewrapper">
        <div className="learntocodecontent">
          <img src={HomepageLogo} className="home-imagewrapper-logo" />
          <h3 className="home-imagewrapper-title">Potential Clients Tracker</h3>
        </div>
        <svg
          style={{ backgroundColor: "white", marginTop: "200px" }}
          width="100%"
          height="150"
          viewBox="0 0 100 100"
          preserveAspectRatio="none"
        >
          <path
            id="wavepath"
            d="M0,0  L110,0C35,150 35,0 0,100z"
            fill="#472B7A"
          ></path>
        </svg>
      </div>
      <Box component="main" sx={homeContainer}>
        <Container sx={homeHeaderContainer}>
          <Typography variant="h2">
            Welcome Back, <br />
            <span className="home-username" onClick={handleUsernameClick}>
              {user ? user.firstname : "CCACC"}!
            </span>
          </Typography>
          <Menu
            anchorEl={anchorEl}
            id="account-menu"
            open={open}
            onClose={handleClose}
            onClick={handleClose}
            PaperProps={{
              elevation: 0,
              sx: {
                overflow: "visible",
                filter: "drop-shadow(0px 2px 8px rgba(0,0,0,0.32))",
                mt: 1.5,
                "& .MuiAvatar-root": {
                  width: 32,
                  height: 32,
                  ml: -0.5,
                  mr: 1,
                },
                "&:before": {
                  content: '""',
                  display: "block",
                  position: "absolute",
                  top: 0,
                  right: 14,
                  width: 10,
                  height: 10,
                  bgcolor: "background.paper",
                  transform: "translateY(-50%) rotate(45deg)",
                  zIndex: 0,
                },
              },
            }}
            transformOrigin={{ horizontal: "right", vertical: "top" }}
            anchorOrigin={{ horizontal: "right", vertical: "bottom" }}
          >
            <MenuItem onClick={handleClose}>
              <Avatar /> Profile
            </MenuItem>
            <MenuItem onClick={handleClose}>
              <Avatar /> My account
            </MenuItem>
            <Divider />
            <MenuItem onClick={handleClose}>
              <ListItemIcon>
                <PersonAdd fontSize="small" />
              </ListItemIcon>
              Add another account
            </MenuItem>
            <MenuItem onClick={handleClose}>
              <ListItemIcon>
                <Settings fontSize="small" />
              </ListItemIcon>
              Settings
            </MenuItem>
            <MenuItem onClick={handleClose}>
              <ListItemIcon>
                <Logout fontSize="small" />
              </ListItemIcon>
              Logout
            </MenuItem>
          </Menu>
          <Box>
            Today is {moment().format("MMMM Do YYYY")}.
            <Box sx={{ my: 1 }} />
            Have a great {moment().format("dddd")}!
          </Box>
        </Container>
        <Divider sx={{ my: 5 }} />
        <ClientsTable clients={clients} />
      </Box>
      <Copyright sx={{ mt: 8, mb: 2 }} />
    </>
  );
};

export default Home;
