import React, { useContext, useState } from "react";
import moment from "moment";
import { Container, Typography, Box, Divider } from "@mui/material";
import Image from "mui-image";
import ClientsTable from "./clients/clientsTable/ClientsTable";
import UserContext from "./userContext";
import Nav from "./Nav";
import PotentialClientTrackerApi from "./api";
import HomepageHeaderPic from "./HomepageHeaderPic.jpg";
import "./Home.css";

const Home = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [clients, setClients] = useState(null);

  React.useEffect(() => {
    const fetchUser = async (id) => {
      try {
        const res = await PotentialClientTrackerApi.getUser(id);
        setUser(res.data.user);
      } catch (e) {
        console.log(e);
      }
    };
    const fetchClients = async () => {
      try {
        const res = await PotentialClientTrackerApi.getAllClients();
        console.log(res.data.clients);
        setClients(res.data.clients);
      } catch (e) {
        console.log(e);
      }
    };
    fetchClients();
    fetchUser(userId);
  }, []);

  const homeContainer = {
    marginTop: 5,
    width: "80%",
    mx: "auto",
  };
  const homeHeaderContainer = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };
  return (
    <>
      <Nav />
      <div className="Home-imagewrapper">
        <Image
          src={HomepageHeaderPic}
          height="580px"
          width="100%"
          fit="cover"
          duration={3000}
          easing="cubic-bezier(0.7, 0, 0.6, 1)"
          showLoading={true}
          errorIcon={true}
          shift="null"
          distance="100px"
          shiftDuration={1500}
          bgColor="inherit"
          alt="potential client tracker homepage image"
        />
        <Image
          src=""
          height="580px"
          width="100%"
          fit="cover"
          duration={3000}
          easing="cubic-bezier(0.7, 0, 0.6, 1)"
          showLoading={true}
          errorIcon={false}
          shift="null"
          distance="100px"
          shiftDuration={1500}
          bgColor="rgba(73, 69, 69, 0.559)"
          alt="potential client tracker homepage image"
          wrapperClassName="Home-imagewrapper-layer"
        />
        {/* <div className="Home-imagewrapper-layer"></div> */}
        <h1 className="Home-imagewrapper-title">
          CCACC
          <br />
          Potential Clients Tracker
        </h1>
      </div>
      <Box component="main" sx={homeContainer}>
        <Container sx={homeHeaderContainer}>
          <Typography variant="h2">
            Welcome Back, <br />
            {user ? user.firstname : "CCACC"}!
          </Typography>
          <Box>
            Today is {moment().format("MMMM Do YYYY")}.
            <Box sx={{ my: 1 }} />
            Have a great {moment().format("dddd")}!
          </Box>
        </Container>
        <Divider sx={{ my: 5 }} />
        <ClientsTable clients={clients} />
      </Box>
    </>
  );
};

export default Home;
