import * as React from "react";
import {
  Button,
  Dialog,
  DialogActions,
  Box,
  DialogContent,
  Slide,
  AppBar,
  Toolbar,
  IconButton,
  Tabs,
  Tab,
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import DialogTitle from "@mui/material/DialogTitle";
import PotentialClientTrackerApi from "../../api";
import AlertBanner from "../../AlertBanner";
import AddClientField from "./AddClientField";

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function AddClient({ open, handleCancel }) {
  const [err, setErr] = React.useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const addClientFormData = {
      firstName: data.get("firstName"),
      lastName: data.get("lastName"),
      email: data.get("email"),
      phone: data.get("phone"),
      address: data.get("address"),
      citizenship: data.get("citizenship"),
      channel: data.get("channel"),
      physician: data.get("physician"),
      diagnosis: data.get("diagnosis"),
      note: data.get("note"),
      daycare: data.get("daycare") === "true" ? true : false,
      medicaid: data.get("medicaid") === "true" ? true : false,
    };
    try {
      await PotentialClientTrackerApi.addClient(addClientFormData);
      window.location.reload(false);
    } catch (e) {
      setErr(e.response.data.error.message);
    }
  };

  const editFormButtons = {
    position: "absolute",
    bottom: "0",
    right: "10px",
    background: "#fafafa",
    width: "100%",
  };
  return (
    <Dialog
      fullScreen
      open={open}
      onClose={handleCancel}
      TransitionComponent={Transition}
    >
      <AppBar sx={{ position: "relative" }}>
        <Toolbar>
          <Tabs
            value="1"
            textColor="secondary"
            indicatorColor="secondary"
            sx={{ ml: 2, flex: 1 }}
          >
            <Tab
              value="1"
              label="CCACC Potential Clients Tracker"
              sx={{ color: "#fff" }}
            />
          </Tabs>
          <IconButton
            edge="start"
            color="inherit"
            onClick={handleCancel}
            aria-label="close"
          >
            <CloseIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
      {err ? (
        <AlertBanner
          severity="error"
          title="Add Client Failed"
          msg={`${err} - please try again`}
        />
      ) : null}
      <DialogTitle sx={{ fontSize: "1.5rem", marginTop: "15px" }}>
        Add New Client
      </DialogTitle>
      <DialogContent>
        <Box component="form" onSubmit={handleSubmit}>
          <AddClientField />

          <DialogActions sx={editFormButtons}>
            <Button onClick={handleCancel}>Cancel</Button>
            <Button type="submit">Submit</Button>
          </DialogActions>
        </Box>
      </DialogContent>
    </Dialog>
  );
}
